import React from "react";
import styled from "styled-components";

const Container = styled.div`
  height: 400px;
  /* background-color: aliceblue; */
  padding: 2rem;
`;

const Title = styled.h2`
  font-size: 30px;
  color: rgb(24, 24, 24);
  font-family: "Comfortaa", cursive;
  margin: 1.5rem 0;
`;

const Form = styled.form`
  /* height: 250px; */
`;

const Wrapper = styled.div`
  margin: 1.2rem 0;
`;

const Label = styled.label`
  font-size: 16px;
  font-weight: 600;
  font-family: "Urbanist", sans-serif;
  display: block;
  margin-bottom: 0.5rem;
`;

const Input = styled.input`
  font-size: 20px;
  font-family: "Urbanist", sans-serif;
  border: 1px solid lightgray;
  border-radius: 2px;
  padding: 0.5rem;
  font-size: 16px;
  width: 50%;
`;

const Button = styled.button`
  color: #fff;
  padding: 0.7rem 3rem;
  border-radius: 5px;
  background-color: teal;
  border: none;
  font-family: "Comfortaa", cursive;
  font-size: 14px;
  font-weight: bold;
  margin: 0.5rem 0;
`;

const AddContact = () => {
  return (
    <Container>
      <Title>Add Contact</Title>
      <Form>
        <Wrapper>
          <Label>Name</Label>
          <Input type="text" name="name" placeholder="Name" />
        </Wrapper>
        <Wrapper>
          <Label>Github</Label>
          <Input type="text" name="email" placeholder="Github Address" />
        </Wrapper>
        <Button>Add</Button>
      </Form>
    </Container>
  );
};

export default AddContact;
