import React, { useState } from "react";
import styled from "styled-components";
import uuid from "react-uuid";
import { useParams, useNavigate } from "react-router-dom";

const Container = styled.div`
  /* height: 400px; */
  /* background-color: aliceblue; */
  padding: 2rem;
`;

const Title = styled.h2`
  font-size: 30px;
  color: rgb(24, 24, 24);
  font-family: "Comfortaa", cursive;
  margin: 1.5rem 0;
`;

const Form = styled.form`
  /* height: 250px; */
  border-bottom: 1px solid lightgray;
`;

const Wrapper = styled.div`
  margin: 1.2rem 0;
`;

const Label = styled.label`
  font-size: 16px;
  font-weight: 600;
  font-family: "Urbanist", sans-serif;
  display: block;
  margin-bottom: 0.5rem;
`;

const Input = styled.input`
  font-size: 18px;
  font-family: "Urbanist", sans-serif;
  border: 1px solid lightgray;
  border-radius: 2px;
  padding: 0.5rem;
  font-size: 16px;
  width: 50%;
`;

const Button = styled.button`
  color: #fff;
  padding: 0.7rem 3rem;
  border-radius: 5px;
  background-color: teal;
  border: none;
  font-family: "Comfortaa", cursive;
  font-size: 14px;
  font-weight: bold;
  margin-bottom: 2rem;
  cursor: pointer;
`;

const EditContact = ({ contacts, setContacts, addContactHandler }) => {
  console.log(contacts);
  // const { id, name, address } = contacts;
  console.log(contacts, "in EditContact");
  // const [editName, setEditName] = useState(name);
  // const [editAddress, setEditAddress] = useState(address);

  // const updateContact = (e) => {
  //   e.preventDefault();
  //   addContactHandler({ id: uuid(), name: name, address: address });
  //   setEditName("");
  //   setEditAddress("");
  // };

  let { id } = useParams();
  // console.log(id, "ididid");

  let navigate = useNavigate();

  // console.log(contacts, "contacts");

  const selectedContact = contacts.filter((contact) => {
    return contact.id === id;
  });

  const [nameEdit, setNameEdit] = useState("");
  const [addressEdit, setAddressEdit] = useState("");

  const addContact = (e) => {
    e.preventDefault();
    addContactHandler({ id: uuid(), name: nameEdit, address: addressEdit });
    setNameEdit("");
    setAddressEdit("");
  };

  return (
    <Container>
      HIHIHIHIHIHIHI
      <Title>Edit Contact</Title>
      <Form>
        <Wrapper>
          <Label>Name</Label>
          <Input
            type="text"
            name="name"
            placeholder="Name"
            value={selectedContact[0].name}
            onChange={(e) => {
              // setEditName(e.target.value);
            }}
            required
          />
        </Wrapper>
        <Wrapper>
          <Label>Github</Label>
          <Input
            type="text"
            name="email"
            placeholder="Github Address"
            value={selectedContact[0].address}
            onChange={(e) => {
              // setEditAddress(e.target.value);
            }}
            required
          />
        </Wrapper>
        <Button>Update</Button>
      </Form>
    </Container>
  );
};

export default EditContact;
