import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./components/Header";
import AddContact from "./components/AddContact";
import ContactList from "./components/ContactList";
import { data } from "./data";
import api from "../src/api/contacts";
import EditContact from "./components/EditContact";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

const App = () => {
  const [contacts, setContacts] = useState(data);

  const addContactHandler = async (newContact) => {
    const request = { ...newContact };
    const response = await api.post("/contacts", request);
    // setContacts([...contacts, newContact]);
    setContacts([response.data, ...contacts]);
  };

  // useEffect(() => {
  //   if (localStorage.getItem("contacts")) {
  //     const getData = JSON.parse(localStorage.getItem("contacts"));
  //     setContacts(getData);
  //   }
  // }, []);

  const retrieveContacts = async () => {
    const response = await api.get("/contacts");
    return response.data;
  };

  useEffect(() => {
    // const getData = JSON.parse(localStorage.getItem("contacts"));
    // if (getData) setContacts(getData);
    const getAllContents = async () => {
      const allContacts = await retrieveContacts();
      if (allContacts) setContacts(allContacts);
    };

    getAllContents();
  }, []);

  useEffect(() => {
    //localStorage.setItem("contacts", JSON.stringify(contacts));
  }, [contacts]);

  const removeContactHandler = async (id) => {
    await api.delete(`/contacts/${id}`);
    const newContactList = contacts.filter((contact) => {
      return contact.id !== id;
    });
    setContacts(newContactList);
  };

  const updateContactHandler = () => {};

  return (
    <>
      <Router>
        <Header />
        <AddContact
          contacts={contacts}
          setContacts={setContacts}
          addContactHandler={addContactHandler}
        />
        <ContactList
          contacts={contacts}
          removeContactHandler={removeContactHandler}
        />

        <Routes>
          <Route path="/edit" element={<EditContact />}></Route>
        </Routes>
      </Router>
    </>
  );
};

export default App;
